// src/Components/LikedSavingsPage.js

import React, { useEffect, useState } from "react";
import axios from "axios";
import { useSelector } from "react-redux";
import { Container, Row, Col, Card, CardBody, CardTitle, CardText, Spinner } from "reactstrap";

const LikedSavingsPage = () => {
  const [likedSavings, setLikedSavings] = useState([]);
  const [loading, setLoading] = useState(false);

  const userEmail = useSelector((state) => state.users.user.email);

  useEffect(() => {
    const fetchLikedSavings = async () => {
      try {
        setLoading(true);
        if (userEmail) {
          const response = await axios.get(`http://localhost:3001/getLikedSavings/${userEmail}`);
          setLikedSavings(response.data);
        }
      } catch (error) {
        console.error("Error fetching liked savings:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchLikedSavings();
  }, [userEmail]);

  return (
    <Container className="mt-5">
      <Row>
        <Col>
          <h2 className="mb-4">‚ù§Ô∏è Your Liked Savings</h2>

          {loading ? (
            <div className="text-center"><Spinner color="primary" /></div>
          ) : likedSavings.length === 0 ? (
            <p className="text-center">You haven't liked any savings yet. ü§î</p>
          ) : (
            likedSavings.map((like, index) => (
              <Card className="mb-3" key={like._id}>
                <CardBody>
                  <CardTitle tag="h5">üêë Liked Saving #{index + 1}</CardTitle>
                  {[
                    ["Monthly Salary", like.savingId.salary],
                    ["Electricity Bill", like.savingId.electricity],
                    ["Water Bill", like.savingId.water],
                    ["Household Expenses", like.savingId.household],
                    ["Personal Use", like.savingId.personal],
                    ["Total Expenses", like.savingId.totalExpenses],
                    ["Remaining Salary", like.savingId.remaining],
                  ].map(([label, value], idx) => (
                    <CardText key={idx}><strong>{label}:</strong> OMR {value}</CardText>
                  ))}
                </CardBody>
              </Card>
            ))
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default LikedSavingsPage;
