import React, { useEffect, useState } from "react";
import {
  Container,
  Row,
  Col,
  Button,
  Input,
  Nav,
  NavItem,
  NavLink,
  FormGroup,
  Label,
  Form,
  Card,
  CardBody,
  CardTitle,
  CardText,
} from "reactstrap";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const navigate = useNavigate();

  const [salary, setSalary] = useState("");
  const [electricity, setElectricity] = useState("");
  const [water, setWater] = useState("");
  const [household, setHousehold] = useState("");
  const [personal, setPersonal] = useState("");
  const [totalRemaining, setTotalRemaining] = useState(0);

  useEffect(() => {
    const data = JSON.parse(localStorage.getItem("savings")) || [];
    const total = data.reduce((acc, item) => acc + Number(item.remaining), 0);
    setTotalRemaining(total);
  }, []);

  const handleSave = (e) => {
    e.preventDefault();
    const totalExpenses =
      Number(electricity) + Number(water) + Number(household) + Number(personal);
    const remaining = Number(salary) - totalExpenses;

    const newEntry = {
      id: Date.now(),
      salary,
      electricity,
      water,
      household,
      personal,
      totalExpenses,
      remaining,
    };

    const existingData = JSON.parse(localStorage.getItem("savings")) || [];
    const updatedData = [...existingData, newEntry];
    localStorage.setItem("savings", JSON.stringify(updatedData));

    navigate("/savings");
  };

  return (
    <Container fluid className="mt-4">
      <Row>

        <Col md={9}>
          {/* Piggy Bank Summary */}
          <Card className="mb-4">
            <CardBody>
              <CardTitle tag="h5">üê∑ Piggy Bank</CardTitle>
              <CardText>Total Remaining Salary from All Users:</CardText>
              <h3>OMR{totalRemaining.toFixed(2)}</h3>
            </CardBody>
          </Card>

          {/* Navigation to SavingsForm */}
          <Button
            color="info"
            className="mb-4"
            onClick={() => navigate("/savings")}
          >
            üìÑ View All Savings
          </Button>

          {/* Form */}
          <h2 className="mb-4">Monthly Budget Form</h2>
          <Form onSubmit={handleSave}>
            <FormGroup>
              <Label for="salary">Monthly Salary</Label>
              <Input
                type="number"
                id="salary"
                value={salary}
                onChange={(e) => setSalary(e.target.value)}
                required
              />
            </FormGroup>
            <FormGroup>
              <Label for="electricity">Electricity Bill</Label>
              <Input
                type="number"
                id="electricity"
                value={electricity}
                onChange={(e) => setElectricity(e.target.value)}
                required
              />
            </FormGroup>
            <FormGroup>
              <Label for="water">Water Bill</Label>
              <Input
                type="number"
                id="water"
                value={water}
                onChange={(e) => setWater(e.target.value)}
                required
              />
            </FormGroup>
            <FormGroup>
              <Label for="household">Household Expenses</Label>
              <Input
                type="number"
                id="household"
                value={household}
                onChange={(e) => setHousehold(e.target.value)}
                required
              />
            </FormGroup>
            <FormGroup>
              <Label for="personal">Personal Use</Label>
              <Input
                type="number"
                id="personal"
                value={personal}
                onChange={(e) => setPersonal(e.target.value)}
                required
              />
            </FormGroup>

            <Button color="success" type="submit" block>
              Save
            </Button>
          </Form>
        </Col>
      </Row>
    </Container>
  );
};

export default Home;
