import React from "react";
import { useSelector } from "react-redux";

const SavingsSummary = () => {
  const transactions = useSelector((state) => state.savings.transactions);
  const goal = useSelector((state) => state.users.user.goal || 1000); // default goal if undefined

  const totalIncome = transactions
    .filter((t) => t.type === "income")
    .reduce((acc, curr) => acc + curr.amount, 0);

  const totalExpense = transactions
    .filter((t) => t.type === "expense")
    .reduce((acc, curr) => acc + curr.amount, 0);

  const currentSavings = totalIncome - totalExpense;
  const progress = ((currentSavings / goal) * 100).toFixed(2);

  return (
    <div>
      <p><strong>Total Income:</strong> ${totalIncome.toFixed(2)}</p>
      <p><strong>Total Expenses:</strong> ${totalExpense.toFixed(2)}</p>
      <p><strong>Current Savings:</strong> ${currentSavings.toFixed(2)}</p>
      <p><strong>Goal Progress:</strong> {progress}%</p>
    </div>
  );
};

export default SavingsSummary;
