import * as yup from "yup";
export const schema = yup.object().shape({
  name: yup.string().defined().required("Name is required"),
  email: yup.string().defined().email("Invalid email format").required("Email is required"),
  password: yup.string().defined().min(6, "Password must be at least 6 characters").required("Password is required"),
  confirmPassword: yup.string().defined().oneOf([yup.ref("password")], "Passwords must match").required("Please confirm your password"),
  age: yup.number().defined().typeError("Age must be a number").min(13, "You must be at least 13").required("Age is required"),
  gender: yup.string().defined().oneOf(["male", "female"], "Gender must be either 'male' or 'female'").required("Gender is required")
});
