import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

// Thunk for registering a new user
export const registerUser = createAsyncThunk("users/registerUser", async (userData) => {
  const response = await axios.post("http://localhost:3001/registerUser", userData);
  return response.data.user;
});

// Thunk for logging in an existing user
export const login = createAsyncThunk("users/login", async (userData) => {
  const response = await axios.post("http://localhost:3001/login", userData);
  return response.data.user;
});

// Thunk for logging out
export const logout = createAsyncThunk("users/logout", async () => {
  await axios.post("http://localhost:3001/logout");
});

const initialState = {
  user: {},
  isLoading: false,
  isSuccess: false,
  isError: false,
};

const userSlice = createSlice({
  name: "users",
  initialState,
  reducers: {
    updateUser: (state, action) => {
      state.user = { ...state.user, ...action.payload };
    },
    resetState: (state) => {
      state.isLoading = false;
      state.isSuccess = false;
      state.isError = false;
    }
  },
  extraReducers: (builder) => {
    builder
      .addCase(registerUser.pending, (state) => {
        state.isLoading = true;
        state.isSuccess = false;
        state.isError = false;
      })
      .addCase(registerUser.fulfilled, (state, action) => {
        state.user = action.payload;
        state.isLoading = false;
        state.isSuccess = true;
      })
      .addCase(registerUser.rejected, (state) => {
        state.isLoading = false;
        state.isError = true;
      })

      .addCase(login.pending, (state) => {
        state.isLoading = true;
        state.isSuccess = false;
        state.isError = false;
      })
      .addCase(login.fulfilled, (state, action) => {
        state.user = action.payload;
        state.isLoading = false;
        state.isSuccess = true;
      })
      .addCase(login.rejected, (state) => {
        state.isLoading = false;
        state.isError = true;
      })

      .addCase(logout.fulfilled, (state) => {
        state.user = {};
        state.isSuccess = false;
        state.isError = false;
        state.isLoading = false;
      });
  },
});

export const { updateUser, resetState } = userSlice.actions;
export default userSlice.reducer;
