import React, { useEffect, useState } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  CardBody,
  CardTitle,
  CardText,
  Button
} from "reactstrap";
import { useNavigate } from "react-router-dom";

const Profile = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState({});

  useEffect(() => {
    // üß† Get user info from localStorage or dummy data
    const storedUser = JSON.parse(localStorage.getItem("userProfile")) || {
      name: "Guest User",
      email: "guest@example.com",
      location: "Unknown"
    };
    setUser(storedUser);
  }, []);

  const handleUpdateProfile = () => {
    navigate("/update-profile");
  };

  return (
    <Container className="mt-5">
      <Row>
        <Col md={{ size: 8, offset: 2 }}>
          <Card>
            <CardBody>
              <CardTitle tag="h4">üë§ User Profile</CardTitle>
              <CardText><strong>Name:</strong> {user.name}</CardText>
              <CardText><strong>Email:</strong> {user.email}</CardText>
              <CardText><strong>Location:</strong> {user.location}</CardText>

              <Button color="primary" onClick={handleUpdateProfile}>
                ‚úèÔ∏è Update Profile
              </Button>
            </CardBody>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Profile;
