// client/src/Components/Profile.jsx

import {
  Form,
  FormGroup,
  Input,
  Label,
  Button,
  Container,
  Row,
  Col,
} from "reactstrap";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { updateUserProfile } from "../Features/UserSlice";
import User from "./User"; // Sidebar component

const Profile = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { user, isLoading } = useSelector((state) => state.users);

  const [profilePic, setProfilePic] = useState(null);
  const [refreshImage, setRefreshImage] = useState(Date.now());

  useEffect(() => {
    if (!user?.email) {
      navigate("/login"); // Redirect if not logged in
    }
  }, [user, navigate]);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setProfilePic(file);
    }
  };

  const handleUpdate = async (e) => {
    e.preventDefault();

    if (!profilePic) {
      alert("Please select a profile picture to upload.");
      return;
    }

    const formData = new FormData();
    formData.append("email", user.email);
    formData.append("profilePic", profilePic);

    try {
      await dispatch(updateUserProfile(formData)).unwrap();
      alert("Profile picture updated successfully!");
      setRefreshImage(Date.now()); // Refresh the profile image
    } catch (error) {
      console.error("Profile update failed:", error);
      alert("Failed to update profile picture.");
    }
  };

  const renderProfileImage = () => {
    if (user.profilePic) {
      return `http://localhost:3001/uploads/${user.profilePic}?t=${refreshImage}`;
    } else {
      return "https://via.placeholder.com/150"; // Default image
    }
  };

  return (
    <Container fluid className="py-5">
      <Row>
        {/* Left Sidebar */}
        <Col md={3}>
          <User />
        </Col>

        {/* Main Form */}
        <Col md={5}>
          <div className="text-center mb-4">
            <img
              src={renderProfileImage()}
              alt="Profile"
              style={{
                width: "180px",
                height: "180px",
                borderRadius: "50%",
                objectFit: "cover",
                boxShadow: "0 0 10px rgba(0, 0, 0, 0.2)",
              }}
            />
          </div>

          <Form onSubmit={handleUpdate}>
            <FormGroup>
              <Label for="profilePic">Upload New Profile Photo</Label>
              <Input
                id="profilePic"
                name="profilePic"
                type="file"
                onChange={handleFileChange}
              />
            </FormGroup>

            <FormGroup className="mt-4">
              <Button
                color="primary"
                type="submit"
                block
                disabled={isLoading}
              >
                {isLoading ? "Updating..." : "Update Profile Picture"}
              </Button>
            </FormGroup>
          </Form>
        </Col>
      </Row>
    </Container>
  );
};

export default Profile;
