import React from "react";
import {
  Form,
  FormGroup,
  Input,
  Label,
  Button,
  Container,
  Row,
  Col,
} from "reactstrap";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import { useDispatch } from "react-redux";
import { registerUser } from "../Features/UserSlice";
import { useNavigate, Link } from "react-router-dom";
import * as yup from "yup";

const schema = yup.object().shape({
  name: yup.string().required("Name is required"),
  email: yup.string().email().required("Email is required"),
  password: yup.string().required("Password is required"),
  goal: yup.number().required("Goal is required"),
});

const Register = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  const onSubmit = async (data) => {
    try {
      await dispatch(registerUser(data));
      navigate("/dashboard");
    } catch (error) {
      console.log("Registration error:", error);
    }
  };

  return (
    <Container className="register-page py-5">
      <Row className="justify-content-center">
        <Col md={6}>
          <h2 className="mb-4 text-center">Create Account</h2>
          <Form onSubmit={handleSubmit(onSubmit)}>
            <FormGroup>
              <Label className="text-danger fw-bold">Name</Label>
              <Input
                type="text"
                placeholder="Enter your full name"
                {...register("name")}
              />
              <p className="text-danger">{errors.name?.message}</p>
            </FormGroup>

            <FormGroup>
              <Label className="text-danger fw-bold">Email</Label>
              <Input
                type="email"
                placeholder="Enter your email address"
                {...register("email")}
              />
              <p className="text-danger">{errors.email?.message}</p>
            </FormGroup>

            <FormGroup>
              <Label className="text-danger fw-bold">Password</Label>
              <Input
                type="password"
                placeholder="Choose a secure password"
                {...register("password")}
              />
              <p className="text-danger">{errors.password?.message}</p>
            </FormGroup>

            <FormGroup>
              <Label className="text-danger fw-bold">Saving Goal ($)</Label>
              <Input
                type="number"
                placeholder="Enter your savings goal"
                {...register("goal")}
              />
              <p className="text-danger">{errors.goal?.message}</p>
            </FormGroup>

            <Button color="success" type="submit" className="w-100">
              Register
            </Button>
          </Form>

          <p className="mt-3 text-center">
            Already have an account? <Link to="/login">Login here</Link>
          </p>
        </Col>
      </Row>
    </Container>
  );
};

export default Register;
