import React, { useEffect, useState } from "react";
import axios from "axios";

const Location = () => {
  const [ip, setIp] = useState("");
  const [location, setLocation] = useState(null);
  const [error, setError] = useState("");

  const apiKey = "at_6a4YSVfDwNpu157xFHaFKeA0TKsSg"; // üîë Your real API key

  // Fetch IP address and location
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Step 1: Get IP
        const ipRes = await axios.get("https://api.ipify.org?format=json");
        const userIp = ipRes.data.ip;
        setIp(userIp);

        // Step 2: Get location
        const locRes = await axios.get(
          `https://geo.ipify.org/api/v2/country?apiKey=${apiKey}&ipAddress=${userIp}`
        );
        setLocation(locRes.data);
      } catch (err) {
        const message =
          err.response?.data?.messages?.[0] ||
          err.response?.data?.message ||
          err.message ||
          "Unknown error";
        setError(`‚ö†Ô∏è Could not fetch location data. ${message}`);
        console.error("Location error:", err);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="Location">
      <p><strong>IP Address:</strong> {ip || "Loading IP address..."}</p>

      {error ? (
        <p className="text-danger">{error}</p>
      ) : location ? (
        <>
          <p><strong>Country:</strong> {location.location.country}</p>
          <p><strong>Region:</strong> {location.location.region}</p>
          <p><strong>ISP:</strong> {location.isp}</p>
        </>
      ) : (
        <p>Loading Geolocation Data...</p>
      )}
    </div>
  );
};

export default Location;
