import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { updateUserProfile } from "../Features/UserSlice";
import {
  Container, Row, Col, Form, FormGroup, Label,
  Input, Button, Spinner
} from "reactstrap";
import Location from "./Location";
import defaultProfileImage from "../Images/profile.png";

const Profile = () => {
  const dispatch = useDispatch();
  const { user, isLoading } = useSelector((state) => state.users);
  const [preview, setPreview] = useState(null);
  const [submitted, setSubmitted] = useState(false);

  // 游대 Submit only profile image
  const handleSubmit = (e) => {
    e.preventDefault();
    const file = e.target.profilePic.files[0];
    if (!file) return alert("Please choose an image");

    const formData = new FormData();
    formData.append("email", user.email); // Send existing values
    formData.append("name", user.name);
    formData.append("profilePic", file);

    dispatch(updateUserProfile(formData));
    setSubmitted(true);
  };

  // 游대 Update preview after submission
  useEffect(() => {
    if (submitted && user?.profilePic) {
      setPreview(`http://localhost:3001/uploads/${user.profilePic}`);
    }
  }, [submitted, user]);

  const imageToDisplay = preview || (user?.profilePic
    ? `http://localhost:3001/uploads/${user.profilePic}`
    : defaultProfileImage);

  return (
    <Container>
      <Row className="justify-content-center">
        <Col md={8}>
          {/* 游녻 User Info Display */}
          <div className="userdisplay text-center">
            <h2 className="register-title">游녻 Your Profile</h2>

            <img
              src={imageToDisplay}
              alt="Profile"
              className="profileImage"
            />

            <p><strong>Name:</strong> {user?.name}</p>
            <p><strong>Email:</strong> {user?.email}</p>

            {/* 游닞 Update Profile Picture */}
            <Form onSubmit={handleSubmit} encType="multipart/form-data">
              <FormGroup>
                <Label for="profilePic">Update Profile Picture</Label>
                <Input
                  type="file"
                  id="profilePic"
                  name="profilePic"
                  accept="image/*"
                  onChange={(e) => {
                    const file = e.target.files[0];
                    if (file) {
                      const reader = new FileReader();
                      reader.onloadend = () => setPreview(reader.result);
                      reader.readAsDataURL(file);
                    }
                  }}
                />
              </FormGroup>

              <Button color="success" type="submit" block disabled={isLoading}>
                {isLoading ? <Spinner size="sm" /> : "Upload New Image"}
              </Button>
            </Form>
          </div>

          {/* 游늸 Location Info */}
          <div className="locationDisplay mt-5 text-center">
            <h3 className="register-title">游늸 Your Location Info</h3>
            <Location />
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default Profile;
