// client/src/App.js
import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./Components/Login";
import Register from "./Components/Register";
import Profile from "./Components/Profile";
import Home from "./Components/Home"; // üî• ADD this import!
import { Container, Row } from "reactstrap";
import { useSelector } from "react-redux";
import './App.css'; // Styling

const App = () => {
  const email = useSelector((state) => state.users.user.email);

  // üîí Small Wrapper Component to Protect Private Routes
  const PrivateRoute = ({ children }) => {
    return email ? children : <Navigate to="/login" />;
  };

  return (
    <Router>
      <div className="page">
        {/* üîù Top Header */}
        <Row>
          {email ? (
            <header className="header">Welcome, {email}</header>
          ) : null}
        </Row>

        {/* üîÑ Main Routes */}
        <main className="main">
          <Routes>
            <Route path="/" element={<Login />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            {/* üîí Protected Routes */}
            <Route path="/home" element={<PrivateRoute><Home /></PrivateRoute>} />
            <Route path="/profile" element={<PrivateRoute><Profile /></PrivateRoute>} />
          </Routes>
        </main>

        {/* üîª Footer */}
        <Row>
          <footer className="footer">
            SaveWell App &copy; {new Date().getFullYear()}
          </footer>
        </Row>
      </div>
    </Router>
  );
};

export default App;
