  return (
    <Container className="py-5">
      <Row className="justify-content-center">
        <Col md={8}>
          <Card className="shadow-sm p-3">
            <CardBody>
              <CardTitle tag="h3" className="text-center mb-4">
                Welcome to SaveWell, {user.name}!
              </CardTitle>
              <CardText className="text-center">
