// Profile.jsx
import {
  Form,
  FormGroup,
  Input,
  Label,
  Button,
  Container,
  Row,
  Col,
} from "reactstrap";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import { updateUserProfile } from "../Features/UserSlice";
import { userValidationsSchema } from "../Validation/UserValidation";
import Location from "./Location";
import profilePlaceholder from "../Images/profile.png";
import loc from "../Images/loc.png";
import "../App.css";
 
const Profile = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  //const { user,isLoading } = useSelector((state) => state.users);
 const {name,isLoading,isError}=useSelector((state)=>state.users);
 const email = useSelector((state) => state.users.user.email);
 const user = useSelector((state) => state.users.user);
const [userName, setUserNamef] = useState(user.name);
const [pwd, setPwd] = useState(user.password);
const [profilePic, setProfilePic] = useState(user.profilePic);
 
 
  //const [profilePic, setProfilePic] = useState(null);
 
/* const {
  register,
  handleSubmit,
  reset: resetForm,
  formState: { errors },
} = useForm({
  resolver: yupResolver(userValidationsSchema),
  defaultValues: {
    name: "",
    password: "",
    confirmPassword: "",
  },
});*/
const {
      register,
      handleSubmit, // Submit the form when this is called
      formState: { errors },
    } = useForm({
      resolver: yupResolver(userValidationsSchema),
    });
 
 
/* useEffect(() => {
  if (user?.email) {
    resetForm({
      name: user.name || "",
      password: user.password || "",
      confirmPassword: user.password || "",
    });
  } else {
    navigate("/login");
  }
}, [user, resetForm, navigate]);
 
 
  const handleUpdate = (data) => {
    const userData = {
      email: user.email,
      name: data.name,
      password: data.password,
      profilePic:profilePic,
    };
 
    console.log("Submitting:", userData);
    dispatch(updateUserProfile(userData)).then(() => {
      alert("Profile Updated.");
      navigate("/profile");
    });
  };*/
const handleUpdate = (event) => {
      event.preventDefault();
 
     
      const userData = {
        email: user.email,
        name: userName,
        password: pwd,
      };
      console.log(userData);
     
      dispatch(updateUserProfile(userData));
      alert("Profile Updated.");
      // Navigate back to the profile page after the update is completed
      navigate("/profile");
    };
 
 
 
 
 
  /*const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) setProfilePic(file);
  };*/
  const handleFileChange = (event) => {
      // Use e.target.files[0] to get the file itself
      const uploadFile = event.target.files[0];
      if (!uploadFile) alert("No file uploaded");
      else setProfilePic(event.target.files[0]);
    };
 
    useEffect(() => {
      if (!user.email) {
        navigate("/login");
      }
    }, [user.email, navigate]);
 
  const renderProfileImage = () => {
    if (profilePic && typeof profilePic === "string") {
      return `http://localhost:3001/uploads/${profilePic}`;
    } else if (profilePic && typeof profilePic === "object") {
      return URL.createObjectURL(profilePic);
    } else {
      return profilePlaceholder;
    }
  };
 
  return (
    <Container className="py-5">
      <Row className="align-items-start">
        <Col md={7} className="text-center">
          <img
            src={renderProfileImage()}
            alt="User"
            className="profileImage mb-4"
            style={{
              width: "200px",
              height: "200px",
              borderRadius: "50%",
              objectFit: "cover",
            }}
          />
 
          <Form onSubmit={handleUpdate}>
            <FormGroup className="text-start">
              <Label>Upload New Profile Photo</Label>
              <Input type="file" onChange={handleFileChange} />
            </FormGroup>
 
            <FormGroup className="text-start">
              <Label htmlFor="name">Full Name</Label>
              <Input
                id="name"
                type="text"
                placeholder="Enter your name"
                /*{...register("name")}*/
                value={userName}
                  {...register("name", {
                    onChange: (e) => {
                      setUserNamef(e.target.value);
                    },
                  })}
               
              />
              {errors.name && <p className="text-danger">{errors.name.message}</p>}
            </FormGroup>
 
            <FormGroup className="text-start">
              <Label htmlFor="password">Password</Label>
              <Input
                id="password"
                type="password"
                placeholder="Enter your password"
                /*{...register("password")}*/
                value={pwd}
                  {...register("password", {
                    onChange: (e) => {
                      setPwd(e.target.value);
                    },
                  })}
               
              />
              {errors.password && (
                <p className="text-danger">{errors.password.message}</p>
              )}
            </FormGroup>
 
            <Button color="primary" type="submit" >
              Update Profile
            </Button>
          </Form>
        </Col>
 
        <Col md={5} className="text-center">
          <img
            src={loc}
            alt="Location Icon"
            className="profileImage mb-4"
            style={{ width: "150px", height: "150px" }}
          />
          <h3>Your Location</h3>
          <Location />
        </Col>
      </Row>
    </Container>
  );
};
 
export default Profile;
