import React, { useState } from "react";
import {
  Container,
  Row,
  Col,
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  Card,
  CardBody,
  CardTitle,
  Alert
} from "reactstrap";
import axios from "axios"; 
import { useSelector } from "react-redux";
import backgroundImage from "../Images/background.jpg"; // ✅ Correct background image import
import "../App.css"; // ✅ make sure basic styling

const Feedback = () => {
  const [message, setMessage] = useState("");
  const [rating, setRating] = useState(0);
  const [submitted, setSubmitted] = useState(false);

  const user = useSelector((state) => state.users.user);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.post("http://localhost:3001/submitFeedback", {
        userName: user.name,
        userEmail: user.email,
        rating,
        feedbackText: message,
      });

      setMessage("");
      setRating(0);
      setSubmitted(true);

      setTimeout(() => {
        setSubmitted(false);
      }, 4000);
    } catch (error) {
      console.error("Error submitting feedback:", error);
      alert("Error submitting feedback. Please try again.");
    }
  };

  return (
    <div
      className="feedback-background" // 🎨 Add a custom class to style it
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        minHeight: "100vh",
        paddingTop: "50px",
      }}
    >
      <Container className="mt-5">
        <Row>
          <Col md={{ size: 8, offset: 2 }}>
            <Card className="shadow-sm">
              <CardBody>
                <CardTitle tag="h4" className="mb-4 text-center">📝 Feedback Form</CardTitle>

                {submitted && (
                  <Alert color="success" className="text-center">
                    🎉 Thank you for your valuable feedback!
                  </Alert>
                )}

                <Form onSubmit={handleSubmit}>
                  {/* Rating Field */}
                  <FormGroup>
                    <Label for="rating">Rate Us:</Label>
                    <div>
                      {[1, 2, 3, 4, 5].map((star) => (
                        <Button
                          key={star}
                          type="button"
                          color={rating >= star ? "warning" : "secondary"}
                          onClick={() => setRating(star)}
                          style={{
                            marginRight: "5px",
                            fontSize: "22px",
                            borderRadius: "50%",
                          }}
                        >
                          ⭐
                        </Button>
                      ))}
                    </div>
                  </FormGroup>

                  {/* Message Field */}
                  <FormGroup>
                    <Label for="message">Your Feedback:</Label>
                    <Input
                      id="message"
                      type="textarea"
                      rows={4}
                      value={message}
                      onChange={(e) => setMessage(e.target.value)}
                      required
                      placeholder="Write your feedback here..."
                    />
                  </FormGroup>

                  <Button color="success" type="submit" block disabled={rating === 0}>
                    Submit
                  </Button>
                </Form>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Feedback;
