import React from "react";
import {
  Container,
  Row,
  Col,
  Form,
  FormGroup,
  Label,
  Input,
  Button,
} from "reactstrap";
import { useDispatch, useSelector } from "react-redux";
import { registerUser, resetState } from "../Features/UserSlice";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { useNavigate } from "react-router-dom";
import { useEffect } from "react";

// Validation schema
const schema = yup.object().shape({
  name: yup.string().required("Name is required"),
  email: yup.string().email("Invalid email").required("Email is required"),
  password: yup.string().min(6).required("Password is required"),
  goal: yup.number().positive().required("Goal is required"),
});

const Register = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const { isSuccess } = useSelector((state) => state.users);

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({ resolver: yupResolver(schema) });

  const onSubmit = (data) => {
    dispatch(registerUser(data));
  };

  useEffect(() => {
    if (isSuccess) {
      navigate("/login");
    }
    return () => {
      dispatch(resetState());
    };
  }, [isSuccess, navigate, dispatch]);

  return (
    <Container className="register-page py-5">
      <Row className="justify-content-center">
        <Col md={6}>
          <h2 className="mb-4 text-center">Create Account</h2>
          <Form onSubmit={handleSubmit(onSubmit)}>
            <FormGroup>
              <Label for="name">Name</Label>
              <Input id="name" {...register("name")} />
              <p className="text-danger">{errors.name?.message}</p>
            </FormGroup>
            <FormGroup>
              <Label for="email">Email</Label>
              <Input id="email" type="email" {...register("email")} />
              <p className="text-danger">{errors.email?.message}</p>
            </FormGroup>
            <FormGroup>
              <Label for="password">Password</Label>
              <Input id="password" type="password" {...register("password")} />
              <p className="text-danger">{errors.password?.message}</p>
            </FormGroup>
            <FormGroup>
              <Label for="goal">Saving Goal ($)</Label>
              <Input id="goal" type="number" {...register("goal")} />
              <p className="text-danger">{errors.goal?.message}</p>
            </FormGroup>
            <Button type="submit" color="primary" block>
              Register
            </Button>
          </Form>
        </Col>
      </Row>
    </Container>
  );
};

export default Register;
