import React, { useState, useEffect } from "react";
import { Label, Input, Container, Row, Col, Button, Form, FormGroup } from "reactstrap";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
//import logo from "../Images/logo-t.png";
import { login } from "../Features/UserSlice";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const { user, isSuccess, isError } = useSelector((state) => state.users);

  const handleLogin = (e) => {
    e.preventDefault();
    dispatch(login({ email, password }));
  };

  useEffect(() => {
    if (isSuccess || user?.email) {
      navigate("/home");
    }
  }, [isSuccess, user, navigate]);

  return (
    <Container>
      <Form onSubmit={handleLogin}>
        <Row>
          <Col md={3}>
            <img src={logo} alt="Logo" />
          </Col>
        </Row>
        <Row>
          <Col md={3}>
            <FormGroup>
              <Label for="email">Email</Label>
              <Input id="email" type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
            </FormGroup>
          </Col>
        </Row>
        <Row>
          <Col md={3}>
            <FormGroup>
              <Label for="password">Password</Label>
              <Input id="password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
            </FormGroup>
          </Col>
        </Row>
        <Row>
          <Col md={3}>
            <Button type="submit">Login</Button>
          </Col>
        </Row>
        <p>No Account? <Link to="/register">Sign Up Now..</Link></p>
      </Form>
    </Container>
  );
};

export default Login;
