import React, { useEffect, useState } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  CardBody,
  CardTitle,
  CardText,
  Button
} from "reactstrap";

const SavingsForm = () => {
  const [entries, setEntries] = useState([]);

  useEffect(() => {
    const data = JSON.parse(localStorage.getItem("savings")) || [];
    setEntries(data);
  }, []);

  const handleClear = () => {
    localStorage.removeItem("savings");
    setEntries([]);
  };

  return (
    <Container className="mt-5">
      <Row>
        <Col>
          <h2>All Savings Submissions</h2>
          <Button color="warning" onClick={handleClear} className="mb-4">
            Clear All
          </Button>
          {entries.length === 0 && <p>No submissions yet.</p>}
          {entries.map((entry, index) => (
            <Card className="mb-3" key={entry.id}>
              <CardBody>
                <CardTitle tag="h5">User #{index + 1}</CardTitle>
                <CardText><strong>Monthly Salary:</strong> ${entry.salary}</CardText>
                <CardText><strong>Electricity Bill:</strong> ${entry.electricity}</CardText>
                <CardText><strong>Water Bill:</strong> ${entry.water}</CardText>
                <CardText><strong>Household Expenses:</strong> ${entry.household}</CardText>
                <CardText><strong>Personal Use:</strong> ${entry.personal}</CardText>
                <CardText><strong>Total Expenses:</strong> ${entry.totalExpenses}</CardText>
                <CardText><strong>Remaining Salary:</strong> ${entry.remaining}</CardText>
              </CardBody>
            </Card>
          ))}
        </Col>
      </Row>
    </Container>
  );
};

export default SavingsForm;
