import React, { useState } from "react";
import {
  Container,
  Row,
  Col,
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  Card,
  CardBody,
  CardTitle,
  Alert
} from "reactstrap";

const Feedback = () => {
  const [message, setMessage] = useState("");
  const [rating, setRating] = useState(0);
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();

    const feedbackData = {
      id: Date.now(),
      message,
      rating,
    };

    const allFeedback = JSON.parse(localStorage.getItem("feedback")) || [];
    allFeedback.push(feedbackData);
    localStorage.setItem("feedback", JSON.stringify(allFeedback));

    setMessage("");
    setRating(0);
    setSubmitted(true);
  };

  return (
    <Container className="mt-5">
      <Row>
        <Col md={{ size: 8, offset: 2 }}>
          <Card>
            <CardBody>
              <CardTitle tag="h4" className="mb-4">üìù Feedback Form</CardTitle>

              {submitted && (
                <Alert color="success">
                  Thank you for your feedback!
                </Alert>
              )}

              <Form onSubmit={handleSubmit}>
                {/* Rating Field */}
                <FormGroup>
                  <Label for="rating">Rate Us</Label>
                  <div>
                    {[1, 2, 3, 4, 5].map((star) => (
                      <Button
                        key={star}
                        type="button"
                        color={rating >= star ? "warning" : "secondary"}
                        onClick={() => setRating(star)}
                        style={{ marginRight: "5px" }}
                      >
                        ‚≠ê
                      </Button>
                    ))}
                  </div>
                </FormGroup>

                {/* Message Field */}
                <FormGroup>
                  <Label for="message">Your Feedback</Label>
                  <Input
                    id="message"
                    type="textarea"
                    rows={4}
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    required
                  />
                </FormGroup>

                <Button color="primary" type="submit" disabled={rating === 0}>
                  Submit
                </Button>
              </Form>
            </CardBody>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Feedback;
