import React, { useEffect } from "react";
import {
  Container,
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  Row,
  Col
} from "reactstrap";

import { useDispatch, useSelector } from "react-redux";
import { registerUser, resetState } from "../Features/UserSlice";
import { useNavigate, Link } from "react-router-dom";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import '../App.css';
import logoSaveWell from "../Images/logoSaveWell.png";
import backgroundImage from '../Images/backgroud.jpg';

// âœ… Schema with Full Validation
const schema = yup.object().shape({
  name: yup.string().required("Name is required"),
  email: yup.string().email("Invalid email").required("Email is required"),
  password: yup.string().min(6, "Password must be at least 6 characters").required("Password is required"),
  confirmPassword: yup.string().oneOf([yup.ref("password")], "Passwords must match").required("Please confirm your password"),
  age: yup.number().typeError("Age must be a number").min(13, "You must be at least 13").required("Age is required"),
  gender: yup.string().oneOf(["male", "female"]).required("Gender is required")
});

const Register = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const userList=useSelector((state)=>state.users.value);
  const { user, isSuccess, isError, isLoading } = useSelector((state) => state.users);

  const {
    register,
    handleSubmit,
    formState: { errors }
  } = useForm({ resolver: yupResolver(schema) });

  const onSubmit = (data) => {
    try{
    const userData = {
      name: data.name,
      email: data.email,
      password: data.password,
      age: data.age,
      gender: data.gender
    };
    console.log("Form Data:",data);
    alert("Valiadtion all good");
    dispatch(registerUser(userData));
    navigate("/login")
  }catch (error){
    console.log("Error",error);
  }
  };

  useEffect(() => {
    if (isSuccess || user?.email) {
      navigate("/home");
    }
  }, [isSuccess, user, navigate]);

  useEffect(() => {
    return () => {
      dispatch(resetState());
    };
  }, [dispatch]);

  return (
    <div
      className="register-background"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      <Container className="register-page py-4">
        <Row className="justify-content-center">
          <Col md={10} lg={8}>
            <h2 className="text-center register-title mb-4">Register for SaveWell</h2>
            <Form onSubmit={handleSubmit(onSubmit)} autoComplete="off">

              <FormGroup>
                <Label for="name" className="register-label">Full Name</Label>
                <Input id="name" type="text" {...register("name")} placeholder="Enter your name" />
                <p className="text-danger">{errors.name?.message}</p>
              </FormGroup>

              <FormGroup>
                <Label for="email" className="register-label">Email</Label>
                <Input id="email" type="email" {...register("email")} placeholder="Enter your email" />
                <p className="text-danger">{errors.email?.message}</p>
              </FormGroup>

              <FormGroup>
                <Label for="password" className="register-label">Password</Label>
                <Input id="password" type="password" {...register("password")} placeholder="Enter your password" />
                <p className="text-danger">{errors.password?.message}</p>
              </FormGroup>

              <FormGroup>
                <Label for="confirmPassword" className="register-label">Confirm Password</Label>
                <Input id="confirmPassword" type="password" {...register("confirmPassword")} placeholder="Re-enter your password" />
                <p className="text-danger">{errors.confirmPassword?.message}</p>
              </FormGroup>

              <FormGroup>
                <Label for="age" className="register-label">Age</Label>
                <Input id="age" type="number" {...register("age")} placeholder="Enter your age" />
                <p className="text-danger">{errors.age?.message}</p>
              </FormGroup>

              <FormGroup tag="fieldset">
                <Label className="register-label">Gender</Label>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" value="male" {...register("gender")} /> Male
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" value="female" {...register("gender")} /> Female
                  </Label>
                </FormGroup>

                <p className="text-danger">{errors.gender?.message}</p>
              </FormGroup>

              {isError && (
                <p className="text-danger">Registration failed. Please try again.</p>
              )}

              <Button type="submit" className="register-button" block disabled={isLoading}>
                {isLoading ? "Registering..." : "Register"}
              </Button>

              <p className="mt-3 text-center">
                Already have an account? <Link to="/login">Login Here</Link>
              </p>
            </Form>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Register;
