// client/src/Components/Profile.jsx

import {
  Container,
  Row,
  Col,
  Form,
  FormGroup,
  Input,
  Label,
  Button,
} from "reactstrap";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { updateUserProfile } from "../Features/userSlice";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import { userValidationsSchema } from "../Validations/UserValidations";
import Location from "./Location";
import loc from "../Images/loc.png";
import userr from "../Images/userr.png";
//import "../Styles/Profilestyle.css";

const Profile = () => {
  const { user, isLoading } = useSelector((state) => state.users);

  const navigate = useNavigate();
  const dispatch = useDispatch();

  const [firstName, setFirstName] = useState(user.fristname || "");
  const [lastName, setLastName] = useState(user.lastname || "");
  const [pwd, setPwd] = useState(user.password || "");
  const [profilePic, setProfilePic] = useState(user.profilePic || null);

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(userValidationsSchema),
  });

  useEffect(() => {
    if (!user?.email) {
      navigate("/login");
    }
  }, [user, navigate]);

  const handleUpdate = async (data) => {
    const formData = new FormData();
    formData.append("email", user.email);
    formData.append("fristname", firstName);
    formData.append("lastname", lastName);
    formData.append("password", pwd);
    if (profilePic && typeof profilePic !== "string") {
      formData.append("profilePic", profilePic);
    }

    try {
      await dispatch(updateUserProfile(formData)).unwrap();
      toast.success("Profile Updated Successfully!");
      navigate("/");
    } catch (error) {
      console.error("Profile update failed:", error);
      toast.error("Failed to update profile.");
    }
  };

  const handleFileChange = (event) => {
    const uploadFile = event.target.files[0];
    if (!uploadFile) {
      toast.error("No file uploaded");
    } else {
      setProfilePic(uploadFile);
    }
  };

  return (
    <Container fluid className="py-5">
      <ToastContainer />
      <Row>
        {/* Left Side: Profile Info */}
        <Col md={7} className="userdisplay text-center">
          <img
            src={userr}
            alt="user"
            className="profileImage mb-4"
            style={{ width: "180px", height: "180px", borderRadius: "50%" }}
          />

          <Col md={8} className="mx-auto">
            <Form onSubmit={handleSubmit(handleUpdate)}>
              <h5>Upload Photo</h5>
              <Input
                type="file"
                name="profilePic"
                className="mb-3"
                onChange={handleFileChange}
              />

              <FormGroup>
                <Label for="fristname">First Name</Label>
                <Input
                  id="fristname"
                  name="fristname"
                  placeholder="First name..."
                  type="text"
                  value={firstName}
                  {...register("firstname", {
                    onChange: (e) => setFirstName(e.target.value),
                  })}
                />
                {errors.firstname && (
                  <p className="text-danger">{errors.firstname.message}</p>
                )}
              </FormGroup>

              <FormGroup>
                <Label for="lastname">Last Name</Label>
                <Input
                  id="lastname"
                  name="lastname"
                  placeholder="Last name..."
                  type="text"
                  value={lastName}
                  {...register("lastname", {
                    onChange: (e) => setLastName(e.target.value),
                  })}
                />
                {errors.lastname && (
                  <p className="text-danger">{errors.lastname.message}</p>
                )}
              </FormGroup>

              <FormGroup>
                <Label for="password">Password</Label>
                <Input
                  id="password"
                  name="password"
                  placeholder="Password..."
                  type="password"
                  value={pwd}
                  {...register("password", {
                    onChange: (e) => setPwd(e.target.value),
                  })}
                />
                {errors.password && (
                  <p className="text-danger">{errors.password.message}</p>
                )}
              </FormGroup>

              <FormGroup className="mt-4">
                <Button color="primary" className="button" type="submit" block>
                  {isLoading ? "Updating..." : "Update Profile"}
                </Button>
              </FormGroup>
            </Form>
          </Col>
        </Col>

        {/* Right Side: Location */}
        <Col md={5} className="locationDisplay text-center">
          <img
            src={loc}
            alt="location"
            className="profileImage mb-4"
            style={{ width: "150px", height: "150px", objectFit: "cover" }}
          />
          <h3>Your Location</h3>
          <Location />
        </Col>
      </Row>
    </Container>
  );
};

export default Profile;
