import React, { useEffect, useState } from "react";
import axios from "axios";

const Location = () => {
  const [ip, setIp] = useState("");
  const [locationData, setLocationData] = useState(null);
  const [error, setError] = useState("");

  const apiKey = "at_6a4YSVfDwNpu157xFHaFKeA0TKsSg";

  const fetchLocation = async () => {
    try {
      const ipResponse = await axios.get("https://api.ipify.org?format=json");
      const clientIp = ipResponse.data.ip;
      setIp(clientIp);

      const geoResponse = await axios.get(
        `https://geo.ipify.org/api/v2/country?apiKey=${apiKey}&ipAddress=${clientIp}`
      );

      setLocationData(geoResponse.data);
    } catch (err) {
      setError("⚠️ Could not fetch location data.");
      console.error(err);
    }
  };

  useEffect(() => {
    fetchLocation();
  }, []);

  return (
    <div className="Location">
      {error ? (
        <p className="text-danger">{error}</p>
      ) : (
        <>
          <p><strong>IP Address:</strong> {ip || "Loading IP..."}</p>
          {locationData ? (
            <>
              <p><strong>Country:</strong> {locationData.location.country}</p>
              <p><strong>Region:</strong> {locationData.location.region}</p>
              <p><strong>ISP:</strong> {locationData.isp}</p>
            </>
          ) : (
            <p>Loading Geolocation Data...</p>
          )}
        </>
      )}
    </div>
  );
};

export default Location;
