import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { updateUserProfile } from "../Features/UserSlice";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import { userValidationsSchema } from "../Validation/UserValidation";
import {
  Container,
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  Row,
  Col,
  Spinner,
} from "reactstrap";
import Location from "./Location";
import defaultProfileImage from "../Images/profile.png";

const Profile = () => {
  const dispatch = useDispatch();
  const { user, isLoading, isError, errorMessage } = useSelector((state) => state.users);
  const [preview, setPreview] = useState(null);
  const [submitted, setSubmitted] = useState(false);

  const {
    register,
    handleSubmit,
    setValue,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(userValidationsSchema),
  });

  // Pre-fill user info
  useEffect(() => {
    if (user) {
      setValue("name", user.name || "");
      setValue("email", user.email || "");
    }
  }, [user, setValue]);

  const onSubmit = (data) => {
    const formData = new FormData();
    formData.append("email", user.email);
    formData.append("name", data.name);
    if (data.password) {
      formData.append("password", data.password);
    }
    if (data.profilePic[0]) {
      formData.append("profilePic", data.profilePic[0]);
    }

    dispatch(updateUserProfile(formData));
    setSubmitted(true);
  };

  useEffect(() => {
    if (submitted && !isLoading && !isError) {
      alert("‚úÖ Profile updated successfully!");
    }
  }, [submitted, isLoading, isError]);

  return (
    <Container>
      <Row className="justify-content-center">
        <Col md={8}>
          <div className="userdisplay text-center">
            <h2 className="register-title">üë§ User Profile</h2>
            <img
              src={
                user?.profilePic
                  ? `http://localhost:3001/uploads/${user.profilePic}`
                  : preview || defaultProfileImage
              }
              alt="Profile"
              className="profileImage"
            />
            <Form onSubmit={handleSubmit(onSubmit)} encType="multipart/form-data">
              <FormGroup>
                <Label for="name">Name</Label>
                <Input type="text" id="name" {...register("name")} />
                {errors.name && <p className="text-danger">{errors.name.message}</p>}
              </FormGroup>

              <FormGroup>
                <Label for="email">Email (read-only)</Label>
                <Input type="email" id="email" {...register("email")} readOnly />
              </FormGroup>

              <FormGroup>
                <Label for="password">New Password (optional)</Label>
                <Input type="password" id="password" {...register("password")} />
                {errors.password && <p className="text-danger">{errors.password.message}</p>}
              </FormGroup>

              <FormGroup>
                <Label for="profilePic">Update Profile Picture</Label>
                <Input
                  type="file"
                  id="profilePic"
                  accept="image/*"
                  {...register("profilePic")}
                  onChange={(e) => {
                    const file = e.target.files[0];
                    if (file) {
                      const reader = new FileReader();
                      reader.onloadend = () => {
                        setPreview(reader.result);
                      };
                      reader.readAsDataURL(file);
                    }
                  }}
                />
              </FormGroup>

              <Button color="success" type="submit" disabled={isLoading} block>
                {isLoading ? <Spinner size="sm" /> : "Update Profile"}
              </Button>
            </Form>
          </div>

          {/* üß≠ Location Section */}
          <div className="locationDisplay mt-5 text-center">
            <h3 className="register-title">üìç Your Location Info</h3>
            <Location />
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default Profile;
