// client/src/App.js
import React from "react";
import { BrowserRouter as Router, Routes, Route, Link, Navigate, useNavigate } from "react-router-dom";
import Login from "./Components/Login";
import Register from "./Components/Register";
import Profile from "./Components/Profile";
import Home from "./Components/Home";
import SavingsForm from "./Components/SavingsForm";
import { Container, Row, Col } from "reactstrap";
import { useSelector, useDispatch } from "react-redux";
import { logout } from "./Features/UserSlice";
import './App.css'; 

const App = () => {
  const email = useSelector((state) => state.users.user.email);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  // üîí Private Route Wrapper
  const PrivateRoute = ({ children }) => {
    return email ? children : <Navigate to="/login" />;
  };

  // üîê Logout Function
  const handleLogout = () => {
    dispatch(logout());
    navigate("/login");
  };

  return (
    <div className="page">
      {/* üîù Top Header */}
      <Row className="header align-items-center">
        <Col>
          {email && (
            <div>
              Welcome, {email}
            </div>
          )}
        </Col>
        <Col className="text-end">
          {email && (
            <div>
              <Link to="/profile" style={{ color: "white", marginRight: "15px" }}>Profile</Link>
              <Link to="/savings" style={{ color: "white", marginRight: "15px" }}>Savings</Link>
              <span
                onClick={handleLogout}
                style={{ color: "white", cursor: "pointer", textDecoration: "underline" }}
              >
                Logout
              </span>
            </div>
          )}
        </Col>
      </Row>

      {/* üîÑ Main Routes */}
      <main className="main">
        <Routes>
          <Route path="/" element={<Login />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/home" element={<PrivateRoute><Home /></PrivateRoute>} />
          <Route path="/profile" element={<PrivateRoute><Profile /></PrivateRoute>} />
          <Route path="/savings" element={<PrivateRoute><SavingsForm /></PrivateRoute>} />
        </Routes>
      </main>

      {/* üîª Footer */}
      <Row>
        <footer className="footer">
          SaveWell App &copy; {new Date().getFullYear()}
        </footer>
      </Row>
    </div>
  );
};

export default App;
