import React, { useEffect, useState } from "react";
import {
  Container,
  Row,
  Col,
  Button,
  Input,
  FormGroup,
  Label,
  Form,
  Card,
  CardBody,
  CardTitle,
  CardText,
} from "reactstrap";
import { useNavigate } from "react-router-dom";
import backgroundImage from "../Images/backgroud.jpg";
import "../App.css";
import axios from "axios"; // ✅ important to add axios

const Home = () => {
  const navigate = useNavigate();

  const [salary, setSalary] = useState("");
  const [electricity, setElectricity] = useState("");
  const [water, setWater] = useState("");
  const [household, setHousehold] = useState("");
  const [personal, setPersonal] = useState("");
  const [totalRemaining, setTotalRemaining] = useState(0);

  useEffect(() => {
    // 📥 fetch total remaining from the database
    const fetchSavings = async () => {
      try {
        const response = await axios.get("http://localhost:3001/getAllSavings");
        const total = response.data.reduce((acc, item) => acc + Number(item.remaining), 0);
        setTotalRemaining(total);
      } catch (error) {
        console.error(error);
        setTotalRemaining(0);
      }
    };

    fetchSavings();
  }, []);

  const handleSave = async (e) => {
    e.preventDefault();
    const totalExpenses =
      Number(electricity) + Number(water) + Number(household) + Number(personal);
    const remaining = Number(salary) - totalExpenses;

    const newEntry = {
      userName: "Anonymous", // ✏️ If you have user info you can use real name
      userEmail: "anonymous@gmail.com",
      salary,
      electricity,
      water,
      household,
      personal,
      totalExpenses,
      remaining,
    };

    try {
      await axios.post("http://localhost:3001/submitSaving", newEntry);
      alert("Saving submitted successfully!");
      navigate("/savings");
    } catch (error) {
      console.error(error);
      alert("Error submitting saving. Please try again.");
    }
  };

  return (
    <div
      className="register-background"
      style={{
        backgroundImage: `url(${backgroundImage})`,
      }}
    >
      <Container className="register-page py-5">
        <Row>
          <Col lg={12}>
            {/* Piggy Bank Summary */}
            <Card className="mb-4">
              <CardBody>
                <CardTitle tag="h5">🐷 Piggy Bank</CardTitle>
                <CardText>Total Remaining Salary from All Users:</CardText>
                <h3>OMR {totalRemaining.toFixed(2)}</h3>
              </CardBody>
            </Card>

            {/* Navigation to Savings Page */}
            <div className="mb-4">
              <Button
                color="info"
                onClick={() => navigate("/savings")}
              >
                📄 View All Savings
              </Button>
            </div>

            {/* Monthly Budget Form */}
            <h2 className="mb-4">Monthly Budget Form</h2>
            <Form onSubmit={handleSave}>
              <FormGroup>
                <Label for="salary" className="register-label">Monthly Salary</Label>
                <Input
                  type="number"
                  id="salary"
                  value={salary}
                  onChange={(e) => setSalary(e.target.value)}
                  required
                />
              </FormGroup>
              <FormGroup>
                <Label for="electricity" className="register-label">Electricity Bill</Label>
                <Input
                  type="number"
                  id="electricity"
                  value={electricity}
                  onChange={(e) => setElectricity(e.target.value)}
                  required
                />
              </FormGroup>
              <FormGroup>
                <Label for="water" className="register-label">Water Bill</Label>
                <Input
                  type="number"
                  id="water"
                  value={water}
                  onChange={(e) => setWater(e.target.value)}
                  required
                />
              </FormGroup>
              <FormGroup>
                <Label for="household" className="register-label">Household Expenses</Label>
                <Input
                  type="number"
                  id="household"
                  value={household}
                  onChange={(e) => setHousehold(e.target.value)}
                  required
                />
              </FormGroup>
              <FormGroup>
                <Label for="personal" className="register-label">Personal Use</Label>
                <Input
                  type="number"
                  id="personal"
                  value={personal}
                  onChange={(e) => setPersonal(e.target.value)}
                  required
                />
              </FormGroup>

              <Button color="success" type="submit" block>
                Save
              </Button>
            </Form>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Home;
