// client/src/Components/Profile.js
import {
  Form,
  FormGroup,
  Input,
  Label,
  Button,
  Container,
  Row,
  Col,
} from "reactstrap";
import { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import { userValidationsSchema } from "../Validation/UserValidation";
import Location from "./Location";
import profile from "../Images/profile.png"; // static profile image
import loc from "../Images/loc.png"; // static location image

const Profile = () => {
  const { isLoading, isError } = useSelector((state) => state.users);
  const user = useSelector((state) => state.users.user);

  const [name, setName] = useState(user?.name || "");
  const [pwd, setPwd] = useState(user?.password || "");
  const [profilePic, setProfilePic] = useState(user?.profilePic || "");

  const navigate = useNavigate();

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(userValidationsSchema),
  });

  const handleFileChange = (event) => {
    const uploadFile = event.target.files[0];
    if (!uploadFile) {
      alert("No file uploaded");
    } else {
      setProfilePic(uploadFile);
    }
  };

  const handleUpdateNavigate = () => {
    navigate("/update-profile");
  };

  useEffect(() => {
    if (!user.email) {
      navigate("/login");
    }
  }, [user.email, navigate]);

  return (
    <Container fluid>
      <Row>
        <Col md={7} className="userdisplay">
          <img src={profile} alt="user" className="profileImage" />

          <Col md={6}>
            <Form>
              <Label>Upload Photo</Label>
              <br />
              <input type="file" name="profilePic" onChange={handleFileChange} />

              <FormGroup>
                <Label for="name">Full Name</Label>
                <Input
                  id="name"
                  name="name"
                  placeholder="Enter your full name..."
                  type="text"
                  value={name}
                  disabled
                  {...register("name")}
                />
              </FormGroup>

              <FormGroup>
                <Label for="password">Password</Label>
                <Input
                  id="password"
                  name="password"
                  placeholder="Enter new password..."
                  type="password"
                  value={pwd}
                  disabled
                  {...register("password")}
                />
              </FormGroup>

              <FormGroup>
                <Button color="primary" className="button" type="button" onClick={handleUpdateNavigate}>
                  Update Profile
                </Button>
              </FormGroup>
            </Form>
          </Col>
        </Col>

        <Col md={4} className="locationDisplay">
          <img src={loc} alt="location" className="profileImage" />
          <h1>YOUR LOCATION</h1>
          <Location />
        </Col>
      </Row>
    </Container>
  );
};

export default Profile;
