import React from "react";
import { useLocation } from "react-router-dom";
import { Container, Row, Col, Card, CardBody, CardTitle, CardText } from "reactstrap";

const SavingsForm = () => {
  const location = useLocation();
  const {
    salary,
    electricity,
    water,
    household,
    personal,
    totalExpenses,
    remaining,
  } = location.state || {};

  return (
    <Container className="mt-5">
      <Row>
        <Col md={{ size: 8, offset: 2 }}>
          <Card>
            <CardBody>
              <CardTitle tag="h3" className="mb-4">Your Savings Summary</CardTitle>
              <CardText><strong>Monthly Salary:</strong> ${salary}</CardText>
              <CardText><strong>Electricity Bill:</strong> ${electricity}</CardText>
              <CardText><strong>Water Bill:</strong> ${water}</CardText>
              <CardText><strong>Household Expenses:</strong> ${household}</CardText>
              <CardText><strong>Personal Use:</strong> ${personal}</CardText>
              <CardText><strong>Total Expenses:</strong> ${totalExpenses}</CardText>
              <CardText><strong>Remaining Salary:</strong> ${remaining}</CardText>
            </CardBody>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default SavingsForm;
