import React, { useEffect, useState } from "react";
import {
  Container,
  Row,
  Col,
  Button,
  Input,
  Nav,
  NavItem,
  NavLink,
  FormGroup,
  Label,
  Form,
  Card,
  CardBody,
  CardTitle,
  CardText,
} from "reactstrap";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const navigate = useNavigate();

  const [salary, setSalary] = useState("");
  const [electricity, setElectricity] = useState("");
  const [water, setWater] = useState("");
  const [household, setHousehold] = useState("");
  const [personal, setPersonal] = useState("");
  const [totalRemaining, setTotalRemaining] = useState(0);

  useEffect(() => {
    const data = JSON.parse(localStorage.getItem("savings")) || [];
    const total = data.reduce((acc, item) => acc + Number(item.remaining), 0);
    setTotalRemaining(total);
  }, []);

  const handleSave = (e) => {
    e.preventDefault();
    const totalExpenses =
      Number(electricity) + Number(water) + Number(household) + Number(personal);
    const remaining = Number(salary) - totalExpenses;

    const newEntry = {
      id: Date.now(),
      salary,
      electricity,
      water,
      household,
      personal,
      totalExpenses,
      remaining,
    };

    const existingData = JSON.parse(localStorage.getItem("savings")) || [];
    const updatedData = [...existingData, newEntry];
    localStorage.setItem("savings", JSON.stringify(updatedData));

    navigate("/savings");
  };

  const handleLike = () => {
    alert("You liked this form! ‚ù§Ô∏è");
  };

  return (
    <Container fluid className="mt-4">
      <Row>
        {/* üéØ Vertical Navbar */}
        <Col md={3} className="sidebar bg-dark text-white vh-100">
          <h4 className="p-3 border-bottom">Home Budget App</h4>
          <Nav vertical pills className="flex-column px-3">
            <NavItem>
              <NavLink href="#profile" className="text-white">
                Profile
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink href="#location" className="text-white">
                Location
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink href="#update-profile" className="text-white">
                Update Profile
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink href="#feedback" className="text-white">
                Feedback
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink href="#logout" className="text-white">
                Logout
              </NavLink>
            </NavItem>
          </Nav>
        </Col>

        {/* üìù Form */}
        <Col md={9}>
          {/* üê∑ Piggy Bank Summary */}
          <Card className="mb-4">
            <CardBody>
              <CardTitle tag="h5">üê∑ Piggy Bank</CardTitle>
              <CardText>Total Remaining Salary from All Users:</CardText>
              <h3>${totalRemaining.toFixed(2)}</h3>
            </CardBody>
          </Card>

          <h2 className="mb-4">Monthly Budget Form</h2>
          <Form onSubmit={handleSave}>
            <FormGroup>
              <Label for="salary">Monthly Salary</Label>
              <Input
                type="number"
                id="salary"
                value={salary}
                onChange={(e) => setSalary(e.target.value)}
                required
              />
            </FormGroup>

            <FormGroup>
              <Label for="electricity">Electricity Bill</Label>
              <Input
                type="number"
                id="electricity"
                value={electricity}
                onChange={(e) => setElectricity(e.target.value)}
                required
              />
            </FormGroup>

            <FormGroup>
              <Label for="water">Water Bill</Label>
              <Input
                type="number"
                id="water"
                value={water}
                onChange={(e) => setWater(e.target.value)}
                required
              />
            </FormGroup>

            <FormGroup>
              <Label for="household">Household Expenses</Label>
              <Input
                type="number"
                id="household"
                value={household}
                onChange={(e) => setHousehold(e.target.value)}
                required
              />
            </FormGroup>

            <FormGroup>
              <Label for="personal">Personal Use</Label>
              <Input
                type="number"
                id="personal"
                value={personal}
                onChange={(e) => setPersonal(e.target.value)}
                required
              />
            </FormGroup>

            <Row>
              <Col md={6}>
                <Button color="success" type="submit" block>
                  Save
                </Button>
              </Col>
              <Col md={6}>
                <Button color="danger" type="button" block onClick={handleLike}>
                  ‚ù§Ô∏è Like
                </Button>
              </Col>
            </Row>
          </Form>
        </Col>
      </Row>
    </Container>
  );
};

export default Home;
