// client/src/Components/Profile.jsx

import {
  Container,
  Row,
  Col,
  Form,
  FormGroup,
  Input,
  Label,
  Button,
} from "reactstrap";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify"; // ✅ import toast
import "react-toastify/dist/ReactToastify.css"; // ✅ import toast CSS
import { updateUserProfile } from "../Features/UserSlice";
import Location from "./Location";
import profilePlaceholder from "../Images/profile.png";
import loc from "../Images/loc.png";
import "../App.css";

const Profile = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { user, isLoading } = useSelector((state) => state.users);

  const [profilePic, setProfilePic] = useState(null);
  const [previewImage, setPreviewImage] = useState(null);
  const [refreshImage, setRefreshImage] = useState(Date.now());

  useEffect(() => {
    if (!user?.email) {
      navigate("/login");
    }
  }, [user, navigate]);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setProfilePic(file);
      setPreviewImage(URL.createObjectURL(file)); // ✅ Create preview
    }
  };

  const handleUpdate = async (e) => {
    e.preventDefault();

    if (!profilePic) {
      toast.error("Please select a profile picture!");
      return;
    }

    const formData = new FormData();
    formData.append("email", user.email);
    formData.append("profilePic", profilePic);

    try {
      await dispatch(updateUserProfile(formData)).unwrap();
      setRefreshImage(Date.now()); // Force image reload
      toast.success("Profile picture updated successfully!"); // ✅ Toast Success
      setPreviewImage(null); // Clear preview after success
    } catch (error) {
      console.error("Profile update failed:", error);
      toast.error("Failed to update profile picture."); // ✅ Toast Error
    }
  };

  const renderProfileImage = () => {
    if (previewImage) {
      return previewImage; // ✅ show preview first if available
    } else if (user.profilePic) {
      return `http://localhost:3001/uploads/${user.profilePic}?t=${refreshImage}`;
    } else {
      return profilePlaceholder;
    }
  };

  return (
    <Container className="py-5">
      <ToastContainer /> {/* ✅ Mount ToastContainer */}
      <Row className="align-items-start">
        {/* Left Section: Profile Info */}
        <Col md={7} className="text-center">
          <img
            src={renderProfileImage()}
            alt="Profile"
            className="profileImage mb-4"
            style={{
              width: "200px",
              height: "200px",
              borderRadius: "50%",
              objectFit: "cover",
              boxShadow: "0 0 10px rgba(0,0,0,0.2)",
            }}
          />

          <h4 className="mb-2">{user.name || "Unknown User"}</h4>
          <p className="text-muted">{user.email || "No Email Provided"}</p>

          <Form onSubmit={handleUpdate} className="mt-4">
            <FormGroup className="text-start">
              <Label>Upload New Profile Photo</Label>
              <Input type="file" onChange={handleFileChange} />
            </FormGroup>

            <Button color="primary" type="submit" disabled={isLoading} block>
              {isLoading ? "Updating..." : "Update Photo"}
            </Button>
          </Form>
        </Col>

        {/* Right Section: Location */}
        <Col md={5} className="text-center">
          <img
            src={loc}
            alt="Location Icon"
            className="profileImage mb-4"
            style={{
              width: "150px",
              height: "150px",
              objectFit: "cover",
            }}
          />
          <h3>Your Location</h3>
          <Location />
        </Col>
      </Row>
    </Container>
  );
};

export default Profile;
