import React, { useEffect, useState } from "react";
import {
  Container,
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  Row,
  Col,
} from "reactstrap";
import { useDispatch, useSelector } from "react-redux";
import { login } from "../Features/UserSlice";
 
import { useNavigate, Link } from "react-router-dom";
import { useForm } from "react-hook-form";
 
import logoSaveWell from '../Images/logoSaveWell.png';
 
 
 
 
const Login = () => {
  const [email,setemail]=useState("");
  const [password,setepassword]=useState("");
  const dispatch = useDispatch();
  const navigate = useNavigate();
 
  const user =useSelector((state)=> state.users.user);
  const isSuccess =useSelector((state)=> state.users.isSuccess);
  const isError = useSelector((state)=>state.users.isError);
 
  const handleLogin = () => {
    const userData = {
      email,
      password,
    };
    dispatch(login(userData))
  };
  useEffect(() => {
    if (isError) {
      navigate("/login");
    } else if (isSuccess) {
      navigate("/home");
    }
  }, [isError, isSuccess]);
 
 
  return (
    <Container className="login-page py-5">
      <Row className="justify-content-center">
        <Col md={6}>
          <h2 className="text-center mb-4" style={{ fontFamily: 'Segoe UI', fontWeight: 'bold' }}>Login to SaveWell</h2>
          <div className="text-center mb-4">
            <img src={logoSaveWell} alt="SaveWell Logo" width="150" />
          </div>
          <Form  autoComplete="off">
            <FormGroup>
              <Label for="email" style={{ color: '#e91e63', fontWeight: '600' }}>Email</Label>
              <Input
                id="email"
                type="email"
                autoComplete="off"
                placeholder="Enter your email"
               
                onChange={(e)=>setemail(e.target.value)}
              />
             
            </FormGroup>
            <FormGroup>
              <Label for="password" style={{ color: '#e91e63', fontWeight: '600' }}>Password</Label>
              <Input
                id="password"
                type="password"
                autoComplete="new-password"
                placeholder="Enter your password"
               
                onChange={(e)=>setepassword(e.target.value)}
              />
             
            </FormGroup>
            {isError && (
              <p className="text-danger">Login failed. Please check your credentials.</p>
            )}
            <Button type="submit"  onClick={()=>handleLogin} style={{ backgroundColor: '#4CAF50', border: 'none' }}  className="button">
              Login
            </Button>
          </Form>
          <p className="mt-3 text-center">
            No Account? <Link to="/register">Sign Up Now..</Link>
          </p>
        </Col>
      </Row>
    </Container>
  );
};
 
export default Login;
