// client/src/Components/Profile.jsx

import {
  Container,
  Row,
  Col,
  Form,
  FormGroup,
  Input,
  Label,
  Button,
} from "reactstrap";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { updateUserProfile } from "../Features/userSlice";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import { userValidationsSchema } from "../Validations/UserValidations";
import Location from "./Location";
import loc from "../Images/loc.png";
import profilePlaceholder from "../Images/profile.png";

const Profile = () => {
  const { user, isLoading } = useSelector((state) => state.users);

  const dispatch = useDispatch();
  const navigate = useNavigate();

  const [firstName, setFirstName] = useState(user.firstname || "");
  const [lastName, setLastName] = useState(user.lastname || "");
  const [pwd, setPwd] = useState(user.password || "");
  const [profilePic, setProfilePic] = useState(user.profilePic || null);
  const [previewImage, setPreviewImage] = useState(null);
  const [refreshImage, setRefreshImage] = useState(Date.now());

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(userValidationsSchema),
  });

  useEffect(() => {
    if (!user?.email) {
      navigate("/login");
    }
  }, [user, navigate]);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setProfilePic(file);
      setPreviewImage(URL.createObjectURL(file));
    }
  };

  const handleUpdate = async () => {
    const formData = new FormData();
    formData.append("email", user.email);
    formData.append("firstname", firstName);
    formData.append("lastname", lastName);
    formData.append("password", pwd);
    if (profilePic && typeof profilePic !== "string") {
      formData.append("profilePic", profilePic);
    }

    try {
      await dispatch(updateUserProfile(formData)).unwrap();
      toast.success("Profile updated successfully!");
      setPreviewImage(null);
      setRefreshImage(Date.now());
    } catch (error) {
      console.error(error);
      toast.error("Failed to update profile.");
    }
  };

  const renderProfileImage = () => {
    if (previewImage) {
      return previewImage;
    } else if (user.profilePic) {
      return `http://localhost:3001/uploads/${user.profilePic}?t=${refreshImage}`;
    } else {
      return profilePlaceholder;
    }
  };

  return (
    <Container fluid className="py-5">
      <ToastContainer />
      <Row>
        {/* Left Side: User Info */}
        <Col md={7} className="text-center">
          <img
            src={renderProfileImage()}
            alt="Profile"
            style={{
              width: "180px",
              height: "180px",
              borderRadius: "50%",
              objectFit: "cover",
              boxShadow: "0 0 10px rgba(0,0,0,0.2)",
            }}
            className="mb-4"
          />

          <Col md={8} className="mx-auto">
            <Form onSubmit={handleSubmit(handleUpdate)}>
              <h5 className="mb-3">Upload New Profile Photo</h5>
              <Input
                type="file"
                name="profilePic"
                className="mb-3"
                onChange={handleFileChange}
              />

              <FormGroup>
                <Label for="firstname">First Name</Label>
                <Input
                  id="firstname"
                  name="firstname"
                  placeholder="Enter first name"
                  type="text"
                  value={firstName}
                  {...register("firstname", {
                    onChange: (e) => setFirstName(e.target.value),
                  })}
                />
                {errors.firstname && (
                  <p className="text-danger">{errors.firstname.message}</p>
                )}
              </FormGroup>

              <FormGroup>
                <Label for="lastname">Last Name</Label>
                <Input
                  id="lastname"
                  name="lastname"
                  placeholder="Enter last name"
                  type="text"
                  value={lastName}
                  {...register("lastname", {
                    onChange: (e) => setLastName(e.target.value),
                  })}
                />
                {errors.lastname && (
                  <p className="text-danger">{errors.lastname.message}</p>
                )}
              </FormGroup>

              <FormGroup>
                <Label for="password">Password</Label>
                <Input
                  id="password"
                  name="password"
                  placeholder="Enter password"
                  type="password"
                  value={pwd}
                  {...register("password", {
                    onChange: (e) => setPwd(e.target.value),
                  })}
                />
                {errors.password && (
                  <p className="text-danger">{errors.password.message}</p>
                )}
              </FormGroup>

              <FormGroup className="mt-4">
                <Button color="primary" type="submit" block>
                  {isLoading ? "Updating..." : "Update Profile"}
                </Button>
              </FormGroup>
            </Form>
          </Col>
        </Col>

        {/* Right Side: Location */}
        <Col md={5} className="text-center">
          <img
            src={loc}
            alt="Location"
            style={{
              width: "150px",
              height: "150px",
              objectFit: "cover",
              marginBottom: "20px",
            }}
          />
          <h3>Your Location</h3>
          <Location />
        </Col>
      </Row>
    </Container>
  );
};

export default Profile;
