// client/src/Components/Profile.jsx

import {
  Container,
  Row,
  Col,
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  Alert,
} from "reactstrap";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { updateUserProfile } from "../Features/UserSlice";

const Profile = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { user, isLoading } = useSelector((state) => state.users);

  const [profilePic, setProfilePic] = useState(null);
  const [updated, setUpdated] = useState(false);
  const [refreshImage, setRefreshImage] = useState(Date.now());

  useEffect(() => {
    if (!user?.email) {
      navigate("/login");
    }
  }, [user, navigate]);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setProfilePic(file);
    }
  };

  const handleUpdate = async (e) => {
    e.preventDefault();

    if (!profilePic) {
      alert("Please select a new profile picture.");
      return;
    }

    const formData = new FormData();
    formData.append("email", user.email);
    formData.append("profilePic", profilePic);

    try {
      await dispatch(updateUserProfile(formData)).unwrap();
      setUpdated(true);
      setRefreshImage(Date.now()); // Refresh picture
    } catch (error) {
      console.error("Profile update failed:", error);
      alert("Failed to update profile picture.");
    }
  };

  const renderProfileImage = () => {
    if (user.profilePic) {
      return `http://localhost:3001/uploads/${user.profilePic}?t=${refreshImage}`;
    } else {
      return "https://via.placeholder.com/150"; // Default if no pic
    }
  };

  return (
    <Container className="py-5">
      <Row className="justify-content-center">
        <Col md={6}>
          <div className="text-center mb-4">
            <img
              src={renderProfileImage()}
              alt="Profile"
              style={{
                width: "180px",
                height: "180px",
                borderRadius: "50%",
                objectFit: "cover",
                boxShadow: "0 0 8px rgba(0, 0, 0, 0.1)",
              }}
            />
          </div>

          <h4 className="text-center">{user.name || "No Name"}</h4>
          <p className="text-center text-muted">{user.email || "No Email"}</p>

          {updated && (
            <Alert color="success" className="text-center">
              Profile picture updated successfully!
            </Alert>
          )}

          <Form onSubmit={handleUpdate} className="mt-4">
            <FormGroup>
              <Label for="profilePic">Upload New Profile Picture</Label>
              <Input
                id="profilePic"
                name="profilePic"
                type="file"
                onChange={handleFileChange}
              />
            </FormGroup>

            <Button color="primary" type="submit" block disabled={isLoading}>
              {isLoading ? "Updating..." : "Update Picture"}
            </Button>
          </Form>
        </Col>
      </Row>
    </Container>
  );
};

export default Profile;
